mapper.select:
select 

from complaint
where cont_id = #{contId,jdbcType=VARCHAR}
  and sno = #{sno,jdbcType=VARCHAR}
  and advi_time = #{adviTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from complaint
where cont_id = #{contId,jdbcType=VARCHAR}
  and sno = #{sno,jdbcType=VARCHAR}
  and advi_time = #{adviTime,jdbcType=TIMESTAMP}

mapper.update:
update complaint


    advi_content = #{adviContent,jdbcType=VARCHAR},


    advi_state = #{adviState,jdbcType=VARCHAR},


where cont_id = #{contId,jdbcType=VARCHAR}
  and sno = #{sno,jdbcType=VARCHAR}
  and advi_time = #{adviTime,jdbcType=TIMESTAMP}

update complaint
set advi_content = #{adviContent,jdbcType=VARCHAR},
  advi_state = #{adviState,jdbcType=VARCHAR}
where cont_id = #{contId,jdbcType=VARCHAR}
  and sno = #{sno,jdbcType=VARCHAR}
  and advi_time = #{adviTime,jdbcType=TIMESTAMP}
